import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Menu, X, Shield, Radar, Database, BarChart3, Grid3x3, Search, Lock } from 'lucide-react';
import './Landing.css';

export default function Landing() {
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeNav, setActiveNav] = useState('home');

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleNavClick = (name, sectionId) => {
    setActiveNav(name);
    setIsMenuOpen(false);

    // Scroll to section or navigate
    if (sectionId === 'login') {
      navigate('/login');
    } else if (sectionId === 'admin') {
      navigate('/admin');
    } else {
      const element = document.getElementById(sectionId);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    }
  };

  const handleLoginToDashboard = () => {
    navigate('/login');
  };

  const features = [
    { icon: Shield, title: 'Secure Access Control', description: 'Role permissions, SSO, audit logs.' },
    { icon: Radar, title: 'Real-Time Tracking', description: 'Status of firearms, radios, vehicles.' },
    { icon: Database, title: 'Centralized Records', description: 'Chain-of-custody and assignments.' },
    { icon: BarChart3, title: 'Reports', description: 'Compliance and readiness KPIs.' },
    { icon: Grid3x3, title: 'Admin Dashboard', description: 'Manage users, stations, policies.' },
    { icon: Search, title: 'Search & Filter', description: 'Locate items by tag or status.' },
  ];

  const overviewStats = [
    { label: 'Checked-out Items', value: '148' },
    { label: 'Service Due', value: '23' },
    { label: 'Compliance', value: '98.2%' },
  ];

  return (
    <div className="landing-page">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-container">
          <div className="navbar-brand">
            <Lock size={24} className="brand-icon" />
            <span className="brand-text">Police Inventory System</span>
          </div>

          {/* Desktop Menu */}
          <ul className="navbar-menu">
            <li>
              <button 
                onClick={() => handleNavClick('home', 'home')}
                className={activeNav === 'home' ? 'nav-link active' : 'nav-link'}
              >
                Home
              </button>
            </li>
            <li>
              <button 
                onClick={() => handleNavClick('features', 'features')}
                className={activeNav === 'features' ? 'nav-link active' : 'nav-link'}
              >
                Features
              </button>
            </li>
            <li>
              <button 
                onClick={() => handleNavClick('login', 'login')}
                className={activeNav === 'login' ? 'nav-link active' : 'nav-link'}
              >
                Login
              </button>
            </li>
            <li>
              <button 
                onClick={() => handleNavClick('admin', 'admin')}
                className={activeNav === 'admin' ? 'nav-link active' : 'nav-link'}
              >
                Admin Panel
              </button>
            </li>
            <li>
              <button 
                onClick={() => handleNavClick('contact', 'contact')}
                className={activeNav === 'contact' ? 'nav-link active' : 'nav-link'}
              >
                Contact Us
              </button>
            </li>
          </ul>

          {/* Mobile Menu Button */}
          <button className="menu-toggle" onClick={toggleMenu}>
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="mobile-menu">
            <button onClick={() => handleNavClick('home', 'home')}>Home</button>
            <button onClick={() => handleNavClick('features', 'features')}>Features</button>
            <button onClick={() => handleNavClick('login', 'login')}>Login</button>
            <button onClick={() => handleNavClick('admin', 'admin')}>Admin Panel</button>
            <button onClick={() => handleNavClick('contact', 'contact')}>Contact Us</button>
          </div>
        )}
      </nav>

      {/* Hero Section */}
      <section className="hero" id="home">
        <div className="hero-container">
          <div className="hero-content">
            <h1 className="hero-title">
              Empowering Law Enforcement with Smarter Inventory Management.
            </h1>
            <p className="hero-subtitle">
              Reliable, accountable, and secure equipment tracking for official police agencies.
            </p>
            <div className="hero-actions">
              <button className="btn btn-primary" onClick={handleLoginToDashboard}>
                Login to Dashboard
              </button>
              <button className="btn btn-secondary" onClick={() => {
                const element = document.getElementById('features');
                element?.scrollIntoView({ behavior: 'smooth' });
              }}>
                Learn More
              </button>
            </div>
          </div>

          <div className="hero-visual">
            <div className="hero-image-placeholder">
              <img src="data:image/svg+xml,%3Csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 600 400'%3E%3Cdefs%3E%3ClinearGradient id='grad1' x1='0%25' y1='0%25' x2='100%25' y2='100%25'%3E%3Cstop offset='0%25' style='stop-color:%233b82f6;stop-opacity:1' /%3E%3Cstop offset='100%25' style='stop-color:%231e40af;stop-opacity:1' /%3E%3C/linearGradient%3E%3C/defs%3E%3Crect width='600' height='400' fill='url(%23grad1)'/%3E%3Crect x='50' y='80' width='140' height='200' fill='%23000' opacity='0.3' rx='8'/%3E%3Crect x='210' y='60' width='160' height='240' fill='%23000' opacity='0.2' rx='8'/%3E%3Crect x='390' y='100' width='130' height='190' fill='%23000' opacity='0.25' rx='8'/%3E%3Ctext x='300' y='220' font-size='32' font-weight='bold' fill='white' text-anchor='middle'%3EPolice Equipment%3C/text%3E%3Ctext x='300' y='260' font-size='18' fill='%23dbeafe' text-anchor='middle'%3ESecure Inventory Management%3C/text%3E%3C/svg%3E" alt="Police Equipment" className="hero-image" />
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="features" id="features">
        <div className="section-container">
          <div className="section-header">
            <h2>Key Capabilities</h2>
            <p>Focused tools for secure inventory oversight.</p>
          </div>

          <div className="features-grid">
            {features.map((feature, index) => {
              const IconComponent = feature.icon;
              return (
                <div key={index} className="feature-card">
                  <div className="feature-icon-wrapper">
                    <IconComponent size={40} className="feature-icon" />
                  </div>
                  <h3>{feature.title}</h3>
                  <p>{feature.description}</p>
                </div>
              );
            })}
          </div>
        </div>
      </section>

      {/* Overview Section */}
      <section className="overview">
        <div className="section-container">
          <h2>Overview</h2>
          <p className="overview-subtitle">Streamlined processes to support field readiness and accountability.</p>
          <p className="overview-description">
            Built for official use by law enforcement agencies. Supports equipment intake, assignment, service intervals, and retirement with consistent audit trails.
          </p>

          {/* Stats Cards */}
          <div className="stats-grid">
            {overviewStats.map((stat, index) => (
              <div key={index} className="stat-card">
                <p className="stat-label">{stat.label}</p>
                <p className="stat-value">{stat.value}</p>
              </div>
            ))}
          </div>

          {/* Chart */}
          <div className="chart-container">
            <div className="chart-bars">
              {[65, 85, 72, 90, 78, 88, 70, 92].map((height, index) => (
                <div
                  key={index}
                  className="chart-bar"
                  style={{ height: `${height}%` }}
                />
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Contact Section */}
      <section className="contact" id="contact">
        <div className="section-container">
          <h2>Contact</h2>
          <p className="contact-subtitle">Official inquiries only.</p>

          <div className="contact-form-wrapper">
            <form className="contact-form">
              <div className="form-group">
                <label>Agency / Department</label>
                <input type="text" placeholder="Enter agency or department name" />
              </div>

              <div className="form-group">
                <label>Official Email</label>
                <input type="email" placeholder="official@agency.gov" />
              </div>

              <div className="form-group">
                <label>Subject</label>
                <input type="text" placeholder="Inquiry subject" />
              </div>

              <div className="form-group">
                <label>Message</label>
                <textarea rows="6" placeholder="Your message here..."></textarea>
              </div>

              <div className="security-notice">
                <strong>Security Notice:</strong> Do not transmit classified information through this form.
              </div>

              <button type="submit" className="btn btn-primary">Send</button>
            </form>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-grid">
            <div className="footer-col">
              <h4>Police Inventory System</h4>
              <p>Official platform for managing law enforcement assets.</p>
            </div>
            <div className="footer-col">
              <h4>Resources</h4>
              <ul>
                <li><a href="#privacy">Privacy Policy</a></li>
                <li><a href="#terms">Terms of Service</a></li>
              </ul>
            </div>
            <div className="footer-col">
              <h4>Contact</h4>
              <ul>
                <li><a href="mailto:contact@agency.gov">contact@agency.gov</a></li>
                <li><a href="tel:(555)010-2277">(555) 010-2277</a></li>
              </ul>
            </div>
          </div>
          <div className="footer-bottom">
            <p>© 2025 Police Inventory System • All rights reserved.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}
