import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Login.css';

const Login = () => {
  const [isRegistering, setIsRegistering] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    rememberMe: false
  });
  const [isLoading, setIsLoading] = useState(false);
  const { login, register, isAuthenticated, user } = useAuth();

  // Redirect if already authenticated
  if (isAuthenticated && user) {
    return <Navigate to={user.role === 'admin' ? '/admin' : '/officer'} replace />;
  }

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    let result;
    if (isRegistering) {
      result = await register({
        firstName: formData.firstName,
        lastName: formData.lastName,
        email: formData.email,
        password: formData.password
      });
    } else {
      result = await login({
        email: formData.email,
        password: formData.password
      });
    }

    setIsLoading(false);
  };

  const switchMode = () => {
    setIsRegistering(!isRegistering);
    setFormData({
      email: '',
      password: '',
      firstName: '',
      lastName: '',
      rememberMe: false
    });
  };

  return (
    <div className="login-wrapper">
      {/* Top Navigation with Logo */}
      <div className="login-navbar">
        <div className="logo-container">
          <div className="logo-icon">◯</div>
          <span className="logo-text">Police Inventory</span>
        </div>
      </div>

      {/* Main Container */}
      <div className="login-container">
        {/* Header Section */}
        <div className="page-header">
          <h1 className="page-title">{isRegistering ? 'Create Account' : 'Sign in'}</h1>
          <p className="page-subtitle">
            {isRegistering 
              ? 'Set up your department account' 
              : 'Access your department dashboard.'}
          </p>
        </div>

        {/* Login Card */}
        <div className="login-card">
          <h2 className="card-title">{isRegistering ? 'New Account' : 'Secure Login'}</h2>
          <p className="card-subtitle">
            {isRegistering 
              ? 'Create a new account with your details' 
              : 'Use your official department email.'}
          </p>

          <form onSubmit={handleSubmit} className="login-form">
            {isRegistering && (
              <>
                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="firstName">First Name</label>
                    <input
                      type="text"
                      id="firstName"
                      name="firstName"
                      className="form-control"
                      value={formData.firstName}
                      onChange={handleChange}
                      required={isRegistering}
                      placeholder="First name"
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="lastName">Last Name</label>
                    <input
                      type="text"
                      id="lastName"
                      name="lastName"
                      className="form-control"
                      value={formData.lastName}
                      onChange={handleChange}
                      required={isRegistering}
                      placeholder="Last name"
                    />
                  </div>
                </div>
              </>
            )}

            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                className="form-control"
                value={formData.email}
                onChange={handleChange}
                required
                placeholder="name@agency.gov"
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                name="password"
                className="form-control"
                value={formData.password}
                onChange={handleChange}
                required
                placeholder="••••••••"
                minLength={isRegistering ? "6" : undefined}
              />
            </div>

            {!isRegistering && (
              <div className="form-group checkbox-group">
                <label htmlFor="rememberMe" className="checkbox-label">
                  <input
                    type="checkbox"
                    id="rememberMe"
                    name="rememberMe"
                    className="checkbox-input"
                    checked={formData.rememberMe}
                    onChange={handleChange}
                  />
                  <span>Remember me</span>
                </label>
              </div>
            )}

            <button
              type="submit"
              className="btn-primary"
              disabled={isLoading}
            >
              {isLoading ? (
                <>
                  <span className="spinner"></span>
                  {isRegistering ? 'Creating Account...' : 'Signing in...'}
                </>
              ) : (
                <>
                  <span className="button-icon">→</span>
                  {isRegistering ? 'Create Account' : 'Sign in'}
                </>
              )}
            </button>
          </form>

          {/* Auth Switch */}
          <div className="auth-switch">
            <p>
              {isRegistering 
                ? 'Already have an account? ' 
                : 'New to Police Inventory? '}
              <button
                type="button"
                className="switch-btn"
                onClick={switchMode}
              >
                {isRegistering ? 'Sign in' : 'Create an account'}
              </button>
            </p>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="login-footer">
        <p className="footer-text">© 2025 Police Inventory. All rights reserved.</p>
        <div className="footer-links">
          <a href="#privacy" className="footer-link">Privacy</a>
          <a href="#terms" className="footer-link">Terms</a>
        </div>
      </footer>
    </div>
  );
};

export default Login;
