import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Menu as MenuIcon,
  X as CloseIcon,
  Shield,
  Radar,
  Database,
  BarChart3,
  Grid3x3,
  Search,
  Lock as LockIcon
} from 'lucide-react';
import './Landing.css';

function Landing() {
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeNav, setActiveNav] = useState('home');

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleNavClick = (name) => {
    setActiveNav(name);
    setIsMenuOpen(false);

    if (name === 'features') {
      const element = document.getElementById('features-section');
      if (element) {
        element.scrollIntoView({ behavior: 'smooth', block: 'start' });
      }
    } else if (name === 'contact') {
      const element = document.getElementById('contact-section');
      if (element) {
        element.scrollIntoView({ behavior: 'smooth', block: 'start' });
      }
    } else if (name === 'login') {
      navigate('/login');
    } else if (name === 'admin') {
      navigate('/admin');
    } else if (name === 'home') {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  const handleLoginClick = () => {
    navigate('/login');
  };

  const handleLearnMoreClick = () => {
    const element = document.getElementById('features-section');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  };

  const featuresData = [
    {
      id: 1,
      icon: Shield,
      title: 'Secure Access Control',
      description: 'Role permissions, SSO, audit logs.'
    },
    {
      id: 2,
      icon: Radar,
      title: 'Real-Time Tracking',
      description: 'Status of firearms, radios, vehicles.'
    },
    {
      id: 3,
      icon: Database,
      title: 'Centralized Records',
      description: 'Chain-of-custody and assignments.'
    },
    {
      id: 4,
      icon: BarChart3,
      title: 'Reports',
      description: 'Compliance and readiness KPIs.'
    },
    {
      id: 5,
      icon: Grid3x3,
      title: 'Admin Dashboard',
      description: 'Manage users, stations, policies.'
    },
    {
      id: 6,
      icon: Search,
      title: 'Search & Filter',
      description: 'Locate items by tag or status.'
    }
  ];

  const statsData = [
    { label: 'Checked-out Items', value: '148' },
    { label: 'Service Due', value: '23' },
    { label: 'Compliance', value: '98.2%' }
  ];

  const chartHeights = [65, 85, 72, 90, 78, 88, 70, 92];

  const handleFormSubmit = (e) => {
    e.preventDefault();
    alert('Thank you for your inquiry. We will contact you soon.');
  };

  return (
    <div className="landing-page-wrapper">
      {/* ===== NAVBAR ===== */}
      <nav className="navbar-fixed">
        <div className="navbar-inner">
          <div className="navbar-brand-section">
            <LockIcon size={28} className="brand-icon" strokeWidth={2.5} />
            <span className="brand-name">Police Inventory System</span>
          </div>

          <ul className="navbar-links-desktop">
            <li>
              <button
                className={`nav-link-btn ${activeNav === 'home' ? 'active' : ''}`}
                onClick={() => handleNavClick('home')}
              >
                Home
              </button>
            </li>
            <li>
              <button
                className={`nav-link-btn ${activeNav === 'features' ? 'active' : ''}`}
                onClick={() => handleNavClick('features')}
              >
                Features
              </button>
            </li>
            <li>
              <button
                className={`nav-link-btn ${activeNav === 'login' ? 'active' : ''}`}
                onClick={() => handleNavClick('login')}
              >
                Login
              </button>
            </li>
            <li>
              <button
                className={`nav-link-btn ${activeNav === 'admin' ? 'active' : ''}`}
                onClick={() => handleNavClick('admin')}
              >
                Admin Panel
              </button>
            </li>
            <li>
              <button
                className={`nav-link-btn ${activeNav === 'contact' ? 'active' : ''}`}
                onClick={() => handleNavClick('contact')}
              >
                Contact Us
              </button>
            </li>
          </ul>

          <button className="menu-toggle-btn" onClick={handleMenuToggle}>
            {isMenuOpen ? <CloseIcon size={28} /> : <MenuIcon size={28} />}
          </button>
        </div>

        {isMenuOpen && (
          <div className="navbar-mobile-menu">
            <button onClick={() => handleNavClick('home')} className="mobile-menu-link">Home</button>
            <button onClick={() => handleNavClick('features')} className="mobile-menu-link">Features</button>
            <button onClick={() => handleNavClick('login')} className="mobile-menu-link">Login</button>
            <button onClick={() => handleNavClick('admin')} className="mobile-menu-link">Admin Panel</button>
            <button onClick={() => handleNavClick('contact')} className="mobile-menu-link">Contact Us</button>
          </div>
        )}
      </nav>

      {/* ===== HERO SECTION ===== */}
      <section className="hero-section" id="home-section">
        <div className="hero-inner">
          <div className="hero-content-left">
            <h1 className="hero-headline">
              Empowering Law Enforcement with Smarter Inventory Management.
            </h1>
            <p className="hero-paragraph">
              Reliable, accountable, and secure equipment tracking for official police agencies.
            </p>
            <div className="hero-button-group">
              <button className="btn-primary" onClick={handleLoginClick}>
                Login to Dashboard
              </button>
              <button className="btn-secondary" onClick={handleLearnMoreClick}>
                Learn More
              </button>
            </div>
          </div>

          <div className="hero-content-right">
            <div className="hero-image-container">
              <svg
                className="hero-svg-image"
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 500 350"
                preserveAspectRatio="xMidYMid slice"
              >
                <defs>
                  <linearGradient id="heroGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                    <stop offset="0%" stopColor="#60a5fa" stopOpacity="1" />
                    <stop offset="100%" stopColor="#1e40af" stopOpacity="1" />
                  </linearGradient>
                </defs>
                <rect width="500" height="350" fill="url(#heroGradient)" />
                <g opacity="0.3">
                  <rect x="40" y="100" width="100" height="20" fill="#000000" rx="3" />
                  <circle cx="45" cy="108" r="6" fill="#000000" />
                  <path d="M 180 120 Q 180 105 195 105 L 215 105 L 215 120 Q 200 125 180 120" fill="#000000" />
                  <path d="M 300 80 L 330 80 L 335 105 Q 335 140 315 160 Q 295 140 295 105 Z" fill="#000000" />
                </g>
                <text x="250" y="280" fontSize="28" fontWeight="bold" fill="white" textAnchor="middle">
                  Police Equipment
                </text>
                <text x="250" y="320" fontSize="16" fill="#93c5fd" textAnchor="middle">
                  Secure Inventory Management
                </text>
              </svg>
            </div>
          </div>
        </div>
      </section>

      {/* ===== FEATURES SECTION ===== */}
      <section className="features-section" id="features-section">
        <div className="section-inner">
          <div className="section-header">
            <h2 className="section-title">Key Capabilities</h2>
            <p className="section-subtitle">Focused tools for secure inventory oversight.</p>
          </div>

          <div className="features-grid">
            {featuresData.map((feature) => {
              const IconComponent = feature.icon;
              return (
                <div key={feature.id} className="feature-card-item">
                  <div className="feature-icon-box">
                    <IconComponent size={44} className="feature-icon" strokeWidth={1.5} />
                  </div>
                  <h3 className="feature-title">{feature.title}</h3>
                  <p className="feature-text">{feature.description}</p>
                </div>
              );
            })}
          </div>
        </div>
      </section>

      {/* ===== OVERVIEW SECTION ===== */}
      <section className="overview-section">
        <div className="section-inner">
          <div className="overview-header">
            <h2 className="section-title">Overview</h2>
            <p className="overview-subtitle">Streamlined processes to support field readiness and accountability.</p>
          </div>

          <p className="overview-description">
            Built for official use by law enforcement agencies. Supports equipment intake, assignment, service intervals, and retirement with consistent audit trails.
          </p>

          <div className="stats-container">
            {statsData.map((stat, index) => (
              <div key={index} className="stat-card-item">
                <p className="stat-label">{stat.label}</p>
                <p className="stat-number">{stat.value}</p>
              </div>
            ))}
          </div>

          <div className="chart-card-container">
            <p className="chart-label">Equipment Status Distribution</p>
            <div className="chart-bars-wrapper">
              {chartHeights.map((height, index) => (
                <div
                  key={index}
                  className="chart-bar-item"
                  style={{ height: `${height}%` }}
                />
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* ===== CONTACT SECTION ===== */}
      <section className="contact-section" id="contact-section">
        <div className="section-inner">
          <div className="contact-header">
            <h2 className="section-title">Contact</h2>
            <p className="contact-subtitle">Official inquiries only.</p>
          </div>

          <div className="contact-form-card">
            <form className="contact-form" onSubmit={handleFormSubmit}>
              <div className="form-field-group">
                <label className="form-label">Agency / Department</label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="Enter your agency name"
                  required
                />
              </div>

              <div className="form-field-group">
                <label className="form-label">Official Email</label>
                <input
                  type="email"
                  className="form-input"
                  placeholder="official@agency.gov"
                  required
                />
              </div>

              <div className="form-field-group">
                <label className="form-label">Subject</label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="Brief subject"
                  required
                />
              </div>

              <div className="form-field-group">
                <label className="form-label">Message</label>
                <textarea
                  className="form-textarea"
                  rows="6"
                  placeholder="Your message here..."
                  required
                />
              </div>

              <div className="security-warning-box">
                <strong>Security Notice:</strong> Do not transmit classified information through this form.
              </div>

              <button type="submit" className="btn-primary btn-full-width">
                Send
              </button>
            </form>
          </div>
        </div>
      </section>

      {/* ===== FOOTER ===== */}
      <footer className="footer-section">
        <div className="footer-inner">
          <div className="footer-columns">
            <div className="footer-column">
              <div className="footer-brand">
                <LockIcon size={24} />
                <span>Police Inventory System</span>
              </div>
              <p className="footer-text">
                A secure, official platform for managing law enforcement equipment with full accountability and compliance tracking.
              </p>
            </div>

            <div className="footer-column">
              <h4 className="footer-title">Resources</h4>
              <ul className="footer-list">
                <li><a href="#privacy" className="footer-link">Privacy Policy</a></li>
                <li><a href="#terms" className="footer-link">Terms of Service</a></li>
                <li><a href="#docs" className="footer-link">Documentation</a></li>
                <li><a href="#support" className="footer-link">Support Center</a></li>
              </ul>
            </div>

            <div className="footer-column">
              <h4 className="footer-title">Contact</h4>
              <ul className="footer-list">
                <li><a href="mailto:contact@agency.gov" className="footer-link"> contact@agency.gov</a></li>
                <li><a href="tel:+15550102277" className="footer-link"> (555) 010-2277</a></li>
                <li className="footer-address"> 123 Precinct Way, Suite 400, Capital City</li>
              </ul>
            </div>
          </div>

          <div className="footer-bottom">
            <p className="footer-copyright"> 2025 Police Inventory Management System. All rights reserved.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Landing;
