import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Menu, X, Shield, Radar, Database, BarChart3, Grid3x3, Search, Lock } from 'lucide-react';

// We no longer import 'Landing.css'
// import './Landing.css'; 

export default function Landing() {
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeNav, setActiveNav] = useState('home');

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleNavClick = (name) => {
    setActiveNav(name);
    setIsMenuOpen(false);

    if (name === 'features') {
      document.getElementById('features')?.scrollIntoView({ behavior: 'smooth' });
    } else if (name === 'contact') {
      document.getElementById('contact')?.scrollIntoView({ behavior: 'smooth' });
    } else if (name === 'login') {
      navigate('/login');
    } else if (name === 'admin') {
      navigate('/admin');
    } else if (name === 'home') {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  // Helper component for nav links to keep the main component clean
  const NavLink = ({ name, label }) => (
    <li>
      <button
        className={`relative bg-none border-none text-sm font-medium text-text-secondary transition-colors duration-300 hover:text-primary 
                   after:content-[''] after:absolute after:bottom-[-6px] after:left-0 after:h-[2px] after:w-0 after:bg-primary after:transition-all after:duration-300
                   ${activeNav === name ? 'text-primary nav-link-active' : ''}`}
        onClick={() => handleNavClick(name)}
      >
        {label}
      </button>
    </li>
  );

  const features = [
    { icon: Shield, title: 'Secure Access Control', description: 'Role permissions, SSO, audit logs.' },
    { icon: Radar, title: 'Real-Time Tracking', description: 'Status of firearms, radios, vehicles.' },
    { icon: Database, title: 'Centralized Records', description: 'Chain-of-custody and assignments.' },
    { icon: BarChart3, title: 'Reports', description: 'Compliance and readiness KPIs.' },
    { icon: Grid3x3, title: 'Admin Dashboard', description: 'Manage users, stations, policies.' },
    { icon: Search, title: 'Search & Filter', description: 'Locate items by tag or status.' },
  ];

  const overviewStats = [
    { label: 'Checked-out Items', value: '148' },
    { label: 'Service Due', value: '23' },
    { label: 'Compliance', value: '98.2%' },
  ];
  
  // Reusable button styles
  const btnBase = "inline-flex items-center justify-center px-7 py-3 rounded-lg text-sm font-semibold transition-all duration-300 ease-in-out";
  const btnPrimary = `${btnBase} bg-primary text-white shadow-lg hover:bg-primary-dark hover:shadow-xl hover:-translate-y-0.5`;
  const btnSecondary = `${btnBase} bg-white text-primary border-2 border-primary hover:bg-primary/10`;

  return (
    <div className="landing-page">
      {/* ========== NAVBAR ========== */}
      <nav className="fixed top-0 left-0 right-0 z-50 h-[70px] bg-white/98 backdrop-blur-lg border-b border-border shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 flex justify-between items-center h-full">
          <a href="#home" onClick={() => handleNavClick('home')} className="flex items-center gap-3 cursor-pointer">
            <Lock size={24} className="text-primary" />
            <span className="text-lg font-bold text-text">Police Inventory System</span>
          </a>

          {/* Desktop Menu */}
          <ul className="hidden md:flex items-center gap-10">
            <NavLink name="home" label="Home" />
            <NavLink name="features" label="Features" />
            <NavLink name="login" label="Login" />
            <NavLink name="admin" label="Admin Panel" />
            <NavLink name="contact" label="Contact Us" />
          </ul>

          {/* Mobile Menu Button */}
          <button className="md:hidden text-text" onClick={toggleMenu}>
            {isMenuOpen ? <X size={26} /> : <Menu size={26} />}
          </button>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden absolute top-[70px] left-0 right-0 bg-white shadow-lg border-t border-border flex flex-col z-40">
            <button className="py-3 px-5 text-left font-medium text-text-secondary hover:bg-gray-100" onClick={() => handleNavClick('home')}>Home</button>
            <button className="py-3 px-5 text-left font-medium text-text-secondary hover:bg-gray-100" onClick={() => handleNavClick('features')}>Features</button>
            <button className="py-3 px-5 text-left font-medium text-text-secondary hover:bg-gray-100" onClick={() => handleNavClick('login')}>Login</button>
            <button className="py-3 px-5 text-left font-medium text-text-secondary hover:bg-gray-100" onClick={() => handleNavClick('admin')}>Admin Panel</button>
            <button className="py-3 px-5 text-left font-medium text-text-secondary hover:bg-gray-100" onClick={() => handleNavClick('contact')}>Contact Us</button>
          </div>
        )}
      </nav>

      {/* ========== HERO SECTION ========== */}
      <section className="relative pt-28 pb-24 md:pt-36 md:pb-32 px-4 sm:px-6 lg:px-8 mt-[70px] bg-gradient-to-br from-bg-light to-white overflow-hidden" id="home">
        {/* Background gradient blob */}
        <div className="absolute top-[-100px] right-[-200px] w-[600px] h-[600px] bg-primary/5 rounded-full blur-3xl -z-10" />
        
        <div className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-12 lg:gap-16 items-center relative z-10">
          <div className="flex flex-col gap-6" style={{ opacity: 0, animation: 'fade-in-up 0.6s ease-out 0.2s forwards' }}>
            <h1 className="text-4xl md:text-5xl lg:text-[56px] font-extrabold text-text tracking-tight leading-tight">
              Empowering Law Enforcement with Smarter Inventory Management.
            </h1>
            <p className="text-lg md:text-xl text-text-secondary max-w-lg">
              Reliable, accountable, and secure equipment tracking for official police agencies.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 mt-4">
              <button className={btnPrimary} onClick={() => navigate('/login')}>Login to Dashboard</button>
              <button className={btnSecondary} onClick={() => document.getElementById('features')?.scrollIntoView({ behavior: 'smooth' })}>Learn More</button>
            </div>
          </div>

          <div className="relative" style={{ opacity: 0, animation: 'fade-in-up 0.6s ease-out 0.4s forwards' }}>
            <div className="w-full aspect-video bg-gradient-to-br from-primary/10 to-primary-dark/5 border-2 border-border rounded-2xl shadow-xl overflow-hidden flex items-center justify-center p-6">
              {/* Cleaner placeholder content */}
              <div className='text-center'>
                <Lock size={60} className="text-primary-light mx-auto" />
                <p className="text-xl font-bold text-primary-dark mt-4">Secure Inventory Dashboard</p>
                <p className="text-sm text-primary">Chain-of-Custody Tracking</p>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* ========== FEATURES SECTION ========== */}
      <section className="py-20 md:py-28 px-4 sm:px-6 lg:px-8 bg-white" id="features">
        <div className="max-w-7xl mx-auto">
          <div className="text-center max-w-2xl mx-auto mb-16">
            <h2 className="text-3xl md:text-4xl lg:text-[42px] font-extrabold text-text mb-4">Key Capabilities</h2>
            <p className="text-lg md:text-xl text-text-secondary">Focused tools for secure inventory oversight.</p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {features.map((feature, index) => {
              const IconComponent = feature.icon;
              return (
                <div 
                  key={index} 
                  className="group bg-white border border-border rounded-xl p-8 transition-all duration-300 ease-in-out hover:shadow-xl hover:-translate-y-2 hover:border-primary cursor-pointer"
                  style={{ opacity: 0, animation: `fade-in-up 0.5s ease-out ${0.1 * index + 0.5}s forwards` }}
                >
                  <div className="w-14 h-14 bg-primary/10 rounded-lg flex items-center justify-center mb-5 transition-all duration-300 group-hover:bg-primary">
                    <IconComponent size={32} className="text-primary transition-all duration-300 group-hover:text-white" />
                  </div>
                  <h3 className="text-lg font-bold text-text mb-2">{feature.title}</h3>
                  <p className="text-base text-text-secondary leading-relaxed">{feature.description}</p>
                </div>
              );
            })}
          </div>
        </div>
      </section>

      {/* ========== OVERVIEW SECTION ========== */}
      <section className="py-20 md:py-28 px-4 sm:px-6 lg:px-8 bg-bg-light">
        <div className="max-w-7xl mx-auto">
          <div className="text-center max-w-3xl mx-auto mb-16">
            <h2 className="text-3xl md:text-4xl lg:text-[42px] font-extrabold text-text mb-4">Overview</h2>
            <p className="text-lg md:text-xl text-text-secondary mb-5">Streamlined processes to support field readiness and accountability.</p>
            <p className="text-base text-text-secondary leading-relaxed">Built for official use by law enforcement agencies. Supports equipment intake, assignment, service intervals, and retirement with consistent audit trails.</p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
            {overviewStats.map((stat, index) => (
              <div key={index} className="bg-white border border-border rounded-xl p-8 text-center transition-all duration-300 hover:shadow-lg hover:border-primary">
                <p className="text-sm font-semibold text-text-secondary mb-2 uppercase tracking-wider">{stat.label}</p>
                <p className="text-4xl md:text-5xl font-extrabold text-primary">{stat.value}</p>
              </div>
            ))}
          </div>

          <div className="bg-white border border-border p-6 md:p-8 rounded-xl shadow-lg">
            <p className="text-base font-semibold text-text mb-6">Equipment Status Distribution</p>
            <div className="flex items-end justify-center gap-2 md:gap-3 h-64">
              {[65, 85, 72, 90, 78, 88, 70, 92, 60, 75, 85, 95].map((height, index) => (
                <div 
                  key={index} 
                  className="flex-1 max-w-[40px] bg-gradient-to-t from-primary to-primary-light rounded-t-md transition-all duration-300 ease-in-out min-h-[8px] hover:from-primary-dark hover:to-primary hover:-translate-y-2" 
                  style={{ height: `${height}%` }} 
                  title={`Category ${index + 1}: ${height}%`}
                />
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* ========== CONTACT SECTION ========== */}
      <section className="py-20 md:py-28 px-4 sm:px-6 lg:px-8 bg-white" id="contact">
        <div className="max-w-7xl mx-auto">
          <div className="text-center max-w-2xl mx-auto mb-16">
            <h2 className="text-3xl md:text-4xl lg:text-[42px] font-extrabold text-text mb-4">Contact</h2>
            <p className="text-lg md:text-xl text-text-secondary">Official inquiries only.</p>
          </div>

          <div className="max-w-2xl mx-auto bg-white border border-border p-8 md:p-10 rounded-xl shadow-lg">
            <form className="flex flex-col gap-5">
              <div className="form-group">
                <label className="text-sm font-semibold text-text mb-1.5 block">Agency / Department</label>
                <input 
                  type="text" 
                  placeholder="Enter your agency name" 
                  className="w-full bg-gray-100 border border-border px-4 py-3 rounded-lg text-sm text-text placeholder:text-gray-400 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-primary focus:bg-white focus:border-primary" 
                />
              </div>

              <div className="form-group">
                <label className="text-sm font-semibold text-text mb-1.5 block">Official Email</label>
                <input 
                  type="email" 
                  placeholder="official@agency.gov" 
                  className="w-full bg-gray-100 border border-border px-4 py-3 rounded-lg text-sm text-text placeholder:text-gray-400 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-primary focus:bg-white focus:border-primary" 
                />
              </div>

              <div className="form-group">
                <label className="text-sm font-semibold text-text mb-1.5 block">Subject</label>
                <input 
                  type="text" 
                  placeholder="Brief subject" 
                  className="w-full bg-gray-100 border border-border px-4 py-3 rounded-lg text-sm text-text placeholder:text-gray-400 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-primary focus:bg-white focus:border-primary" 
                />
              </div>

              <div className="form-group">
                <label className="text-sm font-semibold text-text mb-1.5 block">Message</label>
                <textarea 
                  rows="6" 
                  placeholder="Your message here..." 
                  className="w-full bg-gray-100 border border-border px-4 py-3 rounded-lg text-sm text-text placeholder:text-gray-400 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-primary focus:bg-white focus:border-primary resize-y"
                ></textarea>
              </div>

              <div className="text-sm text-yellow-800 bg-yellow-100 p-4 border-l-4 border-yellow-500 rounded-md">
                <strong>Security Notice:</strong> Do not transmit classified information through this form.
              </div>

              <button type="submit" className={`${btnPrimary} w-full mt-2`}>Send</button>
